mapper.select:
SELECT count(*)
FROM user_bool
WHERE user_id = #{user_id};

mapper.delete:
DELETE
FROM user_bool
WHERE user_id = #{user_id}
  and room_bno = #{room_bno};

mapper.update:
UPDATE user_bool
SET user_id        = #{user_id}
  , room_bno       = #{room_bno}
  , is_coin_return = #{is_coin_return}
  , is_drop        = #{is_drop}
WHERE user_id = #{user_id} and room_bno = #{room_bno}

mapper.select:
SELECT bno
FROM user_bool
WHERE user_id = #{user_id}
  and room_bno = #{room_bno};
