mapper.select:
SELECT count(*)
FROM room_info
WHERE writer = #{writer};

SELECT bno, title, filename,filepath,meet_Date,meet_place,meet_place_road,notice,category,writer,user_limit,user_cnt FROM room_info WHERE bno IN
(null)
#{bno}

mapper.update:
UPDATE room_info
SET title           = #{title}
  , filename        = #{filename}
  , filepath        = #{filepath}
  , meet_Date       = #{meet_Date}
  , meet_place      = #{meet_place}
  , meet_place_road = #{meet_place_road}
  , notice          = #{notice}
  , category        = #{category}
  , up_date         = now()
  , user_limit      = #{user_limit}
  , user_cnt        = #{user_cnt}
WHERE bno = #{bno}

